mapper.insert:
MERGE INTO MATCH_HISTORY
(
	ID
	,USER_ID
	,ARTICLE_ID
	,RANK
	,VIDEO_ID
)
KEY {
	ID
}
VALUES
(
	#{id}
	,#{userId}
	,#{articleId}
	,(SELECT
		IFNULL(MAX(RANK) + 1, 1)
	FROM
		MATCH_HISTORY
	WHERE
		USER_ID = #{userId}
		AND ARTICLE_ID = #{articleId}
	)
	,#{videoId}
)

MERGE INTO MATCH_HISTORY
(
	ID
	,USER_ID
	,ARTICLE_ID
	,VIDEO_ID
)
KEY {
	ID
}
VALUES
(
	#{id}
	,#{userId}
	,#{articleId}
	,#{videoId}
)
