mapper.insert:
INSERT INTO SELECTED_ITEM
(
	ID
	,USER_ID
	,ARTICLE_ID
	,SELECTED_ID
	,VIDEO_ID
)
VALUES
(
	#{id}
	,#{userId}
	,#{articleId}
	,(SELECT
		IFNULL(MAX(SELECTED_ID) + 1, 1)
	FROM
		SELECTED_ITEM
	WHERE
		USER_ID = #{userId}
		AND ARTICLE_ID = #{articleId}
	)
	,#{videoId}
)
